// Minimal React type stubs for offline Next.js builds
export as namespace React;

export type ReactNode = any;
export type ReactElement = any;
export type JSXElementConstructor<P> = (props: P) => any;
export type FC<P = {}> = (props: P & { children?: ReactNode }) => ReactElement | null;

export interface ProviderProps<T> {
  value: T;
  children?: ReactNode;
}

export interface Provider<T> {
  (props: ProviderProps<T>): ReactElement | null;
}

export interface Context<T> {
  Provider: Provider<T>;
  Consumer: any;
  _currentValue?: T;
}

export function createContext<T>(defaultValue: T): Context<T>;
export function useContext<T>(ctx: Context<T>): T;
export function useState<S>(initialState: S | (() => S)): [S, (value: S | ((prev: S) => S)) => void];
export function useEffect(effect: () => void | (() => void), deps?: any[]): void;
export function useMemo<T>(factory: () => T, deps: any[]): T;
export function useRef<T>(initialValue: T | null): { current: T | null };
export function useCallback<T extends (...args: any[]) => any>(callback: T, deps: any[]): T;

export interface Attributes {
  key?: string | number;
}

export interface DOMAttributes<T> {
  children?: ReactNode;
  dangerouslySetInnerHTML?: { __html: string };
  onClick?: any;
  onChange?: any;
}

export interface CSSProperties {
  [key: string]: string | number | undefined;
}

export interface HTMLAttributes<T> extends DOMAttributes<T> {
  className?: string;
  style?: CSSProperties;
  href?: string;
  target?: string;
  rel?: string;
  id?: string;
  lang?: string;
  suppressHydrationWarning?: boolean;
  type?: string;
  value?: any;
  disabled?: boolean;
  placeholder?: string;
}

export interface DetailedHTMLProps<E, T> extends HTMLAttributes<T> {
  ref?: any;
}

export interface FunctionComponent<P = {}> extends FC<P> {}
export type ComponentType<P = {}> = (props: P) => ReactElement | null;

declare global {
  namespace JSX {
    interface IntrinsicElements {
      [elemName: string]: any;
    }
  }
}
